fn main() {
    assert_eq!(Solution::remove_kdigits("1432219".to_string(), 3), "1219");
    assert_eq!(Solution::remove_kdigits("10200".to_string(), 1), "200");
    assert_eq!(Solution::remove_kdigits("10".to_string(), 2), "0");
    assert_eq!(Solution::remove_kdigits("10".to_string(), 1), "0");
    assert_eq!(Solution::remove_kdigits("".to_string(), 1000), "");
    assert_eq!(Solution::remove_kdigits("".to_string(), 555), "");
    assert_eq!(Solution::remove_kdigits("".to_string(), 8222), "");
}

pub struct Solution {}
// https://www.geeksforgeeks.org/build-lowest-number-by-removing-n-digits-from-a-given-number/
impl Solution {
    fn helper(result: &mut String, num: String, k: i32) {
        if k == 0 {
            *result = format!("{}{}", result, num);
            return;
        }
        if num.len() as i32 <= k {
            return;
        }
        let mut min_index = 0;
        for i in 1..=k {
            if num.chars().nth(i as usize) < num.chars().nth(min_index) {
                min_index = i as usize;
            }
        }
        *result = format!("{}{}", result, num.chars().nth(min_index).unwrap());
        Solution::helper(
            result,
            (&num[min_index + 1..]).to_string(),
            k - min_index as i32,
        );
    }
    pub fn remove_kdigits(num: String, k: i32) -> String {
        let mut result = "".to_string();
        Solution::helper(&mut result, num, k);

        let mut ans = "".to_string();
        let mut b = false;
        for c in result.chars() {
            if !(c == '0' && !b) {
                ans = format!("{}{}", ans, c);
                b = true;
            }
        }
        if ans.len() == 0 {
            return "0".to_string();
        }
        return ans;
    }
}
